import java.util.*;
import java.lang.*;

class Solution {
    /**
    For a given list of input numbers, calculate Mean Absolute Deviation
    around the mean of this dataset.
    Mean Absolute Deviation is the average absolute difference between each
    element and a centerpoint (mean in this case):
    MAD = average | x - x_mean |
    >>> mean_absolute_deviation(Arrays.asList(1.0, 2.0, 3.0, 4.0))
    1.0
     */
    public double mean_absolute_deviation(List<Double> numbers) {
        int length = numbers.size();
        if (length == 0) return 0.0;
        if (length == 1) return 0.0;

        double mean = mean(numbers);
        double deviation = 0;
        for (double number : numbers) {
            deviation += Math.abs(number - mean);
        }
        return deviation / length;
    }

    private double mean(List<Double> numbers) {
        double sum = 0;
        for (double number : numbers) {
            sum += number;
        }
        return sum / numbers.size();
    }
}

