    public int car_race_collision(int n) {
        int left = 0, right = n - 1;
        int count = 0;
        while(left < right) {
            if(left + 1 == right) {
                if(left % 2 == 1) {
                    count++;
                }
            } else {
                int cur = left + 1;
                int next = right;
                while(cur < next) {
                    if(cur % 2 == 1) {
                        count++;
                    }
                    cur++;
                }
            }
            left++;
            right--;
        }
        return count;
    }
