    public String anti_shuffle(String s) {
        String[] str = s.split(" ");
        for(int i = 0; i < str.length; i++)
        {
            int[] c = new int[26];
            for(int j = 0; j < str[i].length(); j++)
            {
                c[str[i].charAt(j) - 'a']++;
            }
            StringBuilder sb = new StringBuilder();
            for(int j = 0; j < str[i].length(); j++)
            {
                sb.append((char)(c[str[i].charAt(j) - 'a'] - 1 + 'a'));
            }
            str[i] = sb.toString();
        }
        return String.join(" ", str);
    }
