                if(lst1.size()!= lst2.size()) return lst1.size() - lst2.size();
                for(int i = 0; i < lst1.size(); i++)
                    if(lst1.get(i)!= lst2.get(i))
                        return lst1.get(i) - lst2.get(i);
                return 0;
            }
        });
