        List<String> result = new ArrayList<String>();
        for (int i = 0; i < n; ++i) {
            result.add(Integer.toString(i));
            result.add(Integer.toString(odd(lst.get(i))));
        }
        return result;
    }

    private int odd(String str) {
        int sum = 0;
        for (char c : str.toCharArray()) {
            if (c == '0') {
                ++sum;
            }
        }
        return sum % 2;
    }
}

