class Solution {
    /**
    Given a list of numbers, return whether or not they are sorted
    in ascending order. If list has more than 1 duplicate of the same
    number, return false. Assume no negative numbers and only integers.

    Examples
    is_sorted(Arrays.asList(5)) -> true
    is_sorted(Arrays.asList(1, 2, 3, 4, 5)) -> true
    is_sorted(Arrays.asList(1, 3, 2, 4, 5)) -> false
    is_sorted(Arrays.asList(1, 2, 3, 4, 5, 6)) -> true
    is_sorted(Arrays.asList(1, 2, 3, 4, 5, 6, 7)) -> true
    is_sorted(Arrays.asList(1, 3, 2, 4, 5, 6, 7)) -> false
    is_sorted(Arrays.asList(1, 2, 2, 3, 3, 4)) -> true
    is_sorted(Arrays.asList(1, 2, 2, 2, 3, 4)) -> false
     */
    public boolean is_sorted(List<Integer> lst) {
        if (lst == null)
            return true;
        if (lst.size() == 0)
            return true;
        if (lst.size() == 1)
            return true;

        int i = 1;
        for (int j = 2; j < lst.size(); j++) {
            if (lst.get(i - 1) > lst.get(j))
                return false;
            i++;
        }
        return true;
    }
}

