import java.util.*;
import java.lang.*;

class Solution {
    /**
    For a given list of input numbers, calculate Mean Absolute Deviation
    around the mean of this dataset.
    Mean Absolute Deviation is the average absolute difference between each
    element and a centerpoint (mean in this case):
    MAD = average | x - x_mean |
    >>> meanAbsoltueDeviation(Arrays.asList(1.0, 2.0, 3.0, 4.0))
    1.0
     */
    public double meanAbsoltueDeviation(List<Double> numbers) {
        // The median of the dataset is the center point
        double median = median(numbers);
        // The mean of the dataset is the sum of all the numbers
        double sum = sum(numbers);
        // Find the mean of the absolute deviations of the dataset
        double mean = 0.0;
        for (double number : numbers) {
            double abs = Math.abs(number - median);
            mean += abs;
        }
        mean = mean / numbers.size();
        // Calculate the MAD
        double mad = 0.0;
        for (double number : numbers) {
            double abs = Math.abs(number - median);
            double absDiff = abs - mean;
            mad += absDiff * absDiff;
        }
        return Math.sqrt(mad) / Math.sqrt(numbers.size());
    }

    /**
    Return the median of the list of values.
    >>> median(Arrays.asList(1, 2, 3, 4))
    2.0
     */
    public double median(List<Double> values) {
        int length = values.size();
        // Create an array of length length containing the elements of the list
        double[] array = new double[length];
        for (int i = 0; i < length; i++) {
            array[i] = values.get(i);
        }
        // Sort the array
        Arrays.sort(array);
        // Return the middle element
        return array[length / 2];
    }

    /**
    Return the sum of the list of values.
    >>> sum(Arrays.asList(1, 2, 3, 4))
    9.0
     */
    public double sum(List<Double> values) {
        double sum = 0.0;
        for (double number : values) {
            sum += number;
        }
        return sum;
    }
