import java.lang.*;

class Solution {
    /**
    Given length of a side and high return area for a triangle.
    >>> triangleArea(5, 3)
    7.5
     */
    public double triangleArea(double a, double h) {
        double area = 0;
        if (a <= 0 || h <= 0) {
            return -1;
        }
        if (a == h) {
            return 0;
        }
        if (a == 1) {
            return h;
        }
        if (h == 1) {
            return a;
        }
        for (int i = 1; i < a; i++) {
            double temp = (i + 1) * (i + 1);
            area += (h / 2) * temp;
        }
        area += ((h * (a - 1)) / 2);
        area += (h * (a - 1) + 1) / 2;
        return area;
    }
}

