class Solution {
    /**
    Checks if given string is a palindrome
    >>> isPalindrome("")
    true
    >>> isPalindrome("aba")
    true
    >>> isPalindrome("aaaaa")
    true
    >>> isPalindrome("zbcd")
    false
     */
    public boolean isPalindrome(String text) {
        text = text.toLowerCase();
        int i=0, j=text.length()-1;
        while(i<j){
            while(i<text.length() &&!Character.isLetterOrDigit(text.charAt(i))){
                i++;
            }
            while(j>=0 &&!Character.isLetterOrDigit(text.charAt(j))){
                j--;
            }
            if(i>j){
                break;
            }
            if(!Character.toLowerCase(text.charAt(i)).equals(Character.toLowerCase(text.charAt(j)))){
                return false;
            }
            i++;
            j--;
        }
        return true;
    }
}

