     */
    public List<Object> reveresDelete(String s, String c) {
        List<Object> list = new ArrayList<Object>();
        int[] cnt = new int[256];
        for(char c1:c.toCharArray()) {
            cnt[c1]++;
        }
        StringBuilder sb = new StringBuilder(s);
        for(int i=0; i<s.length(); i++) {
            char c = s.charAt(i);
            if(--cnt[c] < 0) {
                sb.deleteCharAt(i);
