        boolean flag = true;
        for(int i=0; i<sb.length(); i++) {
            char c = sb.charAt(i);
            if(--cnt[c] < 0) {
                flag = false;
                break;
            }
        }
        list.add(sb.toString());
        list.add(flag);
        return list;
    }
