        Stack<Character> stack = new Stack<>();
        for(String s: lst) {
            for(char c: s.toCharArray()) {
                if(c == ')') stack.push(c);
                else if(c == '(') {
                    if(stack.empty()) return "No";
                    stack.pop();
                }
            }
        }
        return stack.empty()? "Yes": "No";
    }
}
