import java.util.*;
import java.lang.*;

class Solution {
    /**
    You are given an array arr of integers and you need to return
    sum of magnitudes of integers multiplied by product of all signs
    of each number in the array, represented by 1, -1 or 0.
    Note: return None for empty arr.

    Example:
    >>> prodSigsn(Arrays.asList(1, 2, 2, -4)) == -9
    >>> prodSigsn(Arrays.asList(0, 1)) == 0
    >>> prodSigsn(Arrays.asList()) == None
     */
    public Optional<Integer> prodSigsn(List<Integer> arr) {
        int n = arr.size();
        if (n == 0) return Optional.empty();
        int res = 1;
        for (int i = 0; i < n; i++) {
            int num = arr.get(i);
            if (num > 0) res *= num;
            else if (num < 0) res *= -num;
            else if (num == 0) res = 0;
        }
        return Optional.of(res);
    }
}

