        StringBuilder sb = new StringBuilder();
        for(int i = 0; i < words.length; i++) {
            int idx = -1;
            while(idx == -1) {
                idx = (int)Math.floor(Math.random() * primes.size());
            }
            sb.append(words[i]).append(" ");
            primes.remove(idx);
        }
        return sb.toString().trim();
    }
