    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        List<Integer> max_levels = new ArrayList<Integer>();
        Stack<Integer> stack = new Stack<Integer>();
        for(int i=0; i<paren_string.length(); i++) {
            char c = paren_string.charAt(i);
            if(c == '(') {
                stack.push(i);
            }
            else if(c == ')') {
                if(stack.size() == 0) {
                    return null;
                }
                else {
                    stack.pop();
                }
            }
            else if(stack.size() > 0) {
                int top = stack.peek();
                if(top > i) {
                    int tmp = i;
                    for(int j=i; j<top; j++) {
                        paren_string.charAt(tmp) == '('? stack.push(tmp) : stack.pop();
                        tmp++;
                    }
                }
            }
        }
        return max_levels;
    }
