        int digit = decimal;
        while(digit > 0) {
            binary.insert(0, (digit % 2) + "");
            digit = (digit / 2);
        }
        int index = binary.length() - 1;
        while(index > 0 && binary.charAt(index) == '0') {
            index--;
        }
        if(index == 0) {
            binary.insert(0, '0');
        }
        else {
