    nextSmallest(Arrays.asList(1, 2, 3, 4, 5)) == Optional[2]
    nextSmallest(Arrays.asList(5, 1, 4, 3, 2)) == Optional[2]
    nextSmallest(Arrays.asList()) == Optional.empty
    nextSmallest(Arrays.asList(1, 1)) == Optional.empty
     */
    public Optional<Integer> nextSmallest(List<Integer> lst) {
        if (lst == null || lst.isEmpty()) {
            return Optional.empty();
        }

        if (lst.size() == 1) {
            return Optional.of(lst.get(0));
        }

        TreeMap<Integer, List<Integer>> tree = new TreeMap<>();

        for (int i = 0; i < lst.size(); i++) {
            if (tree.containsKey(lst.get(i))) {
                tree.get(lst.get(i)).add(lst.get(i));
            } else {
                tree.put(lst.get(i), new ArrayList<>());
                tree.get(lst.get(i)).add(lst.get(i));
            }
        }

        Iterator<Map.Entry<Integer, List<Integer>>> it = tree.entrySet().iterator();

        while (it.hasNext()) {
            Map.Entry<Integer, List<Integer>> entry = it.next();

            if (entry.getValue().size() > 1) {
                it.remove();
                return Optional.of(entry.getValue().get(1));
            }
        }

        return Optional.empty();
    }
}

