            return Optional.empty();
        }

        if (lst.size() == 1) {
            return Optional.of(lst.get(0));
        }

        List<Integer> sorted = new ArrayList<>();

        sorted.addAll(lst);
        Collections.sort(sorted);

        int second = sorted.get(1);

        int i = 1;
        while (i < sorted.size() - 1) {
            if (sorted.get(i) < second) {
                second = sorted.get(i);
            }

            i++;
        }

        return Optional.of(second);
    }
