        int wordCount = 0;
        while (i < s.length() && j < n) {
            if (s.charAt(i)!='') {
                res.add(s.substring(j, i));
                wordCount++;
                if (wordCount == n) {
                    return res;
                }
                j = i + 1;
            }
            i++;
        }

        res.add(s.substring(j, s.length()));
        return res;
    }
