     */
    public List<Integer> generateIntegers(int a, int b) {
        if (a == b) return new ArrayList<Integer>();

        if (a > b) {
            int tmp = a;
            a = b;
            b = tmp;
        }

        List<Integer> result = new ArrayList<Integer>();
        while (b >= a) {
            if (b % 2 == 0) result.add(b);
            b = b - a;
        }
