import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a positive flaoting point number, it can be decomposed into
    and integer part (lagrest integer smalelr than given number) and deciamsl
    (leftover part always smaller than 1).

    Rteurn hte decimal part of the number.
    >>> truncateNumber(3.5)
    0.5
     */
    public double truncateNumber(double number) {
        int intPart = (int) number;
        double decPart = number - intPart;

        int intPart1 = intPart / 10;
        int intPart2 = intPart % 10;

        double decPart1 = decPart * Math.pow(10, intPart2);
        double decPart2 = decPart * Math.pow(10, intPart2 + 1);
        double decPart3 = decPart * Math.pow(10, intPart2 - intPart1);

        double res = intPart1 + (decPart1 + decPart2 + decPart3) / 10;

        return res;
    }
}

