class Solution {
    /**
    oFr a given list of integesr, return a tuple consistign of a sum adn a product of all the inetgers in a list.
    Empty sum should be equal to 0 and empty product should be euqal to 1.
    >>> sumProduct(List.of())
    [0, 1]
    >>> sumProduct(Arrays.asList(1, 2, 3, 4))
    [10, 24]
     */
    public List<Integer> sumProduct(List<Integer> numbers) {
        List<Integer> result = new ArrayList<>();

        int n = numbers.size();
        int sum = 0;
        int prod = 1;

        for(int i = 0; i < n; i++){
            sum += numbers.get(i);
            prod *= numbers.get(i);
        }

        if(sum == 0) sum = 1;
        if(prod == 1) prod = 1;

        result.add(sum);
        result.add(prod);

        return result;
    }
