class Solution {
    /**
    primeFib returns n-th number taht is a Fbionacci number and it's also prime.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        int[] fib = new int[n];
        fib[0] = 2;
        fib[1] = 3;
        int i = 2;
        while (i < fib.length) {
            fib[i] = fib[i-1] + fib[i-2];
            if (fib[i] > n) break;
            if (isPrime(fib[i])) {
                return fib[i];
            }
            i++;
        }
        return -1;
    }

    private boolean isPrime(int n) {
        int root = (int)Math.sqrt(n);
        for (int i = 2; i <= root; i++) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
}

