                return result;
            }
            cur = findMin(cur, grid.get(i));
            if (cur == null) {
                return result;
            }
            if (cur.size() == k) {
                result.addAll(cur);
                cur = grid.get(i);
            }
        }
        return result;
    }

    private List<Integer> findMin(List<Integer> left, List<Integer> right) {
        if (left == null || right == null) {
            return null;
        }
        if (left.size() < right.size()) {
            return left;
        }
        if (left.size() > right.size()) {
            return right;
        }
        int i = 0;
        while (i < left.size() && i < right.size()) {
            if (left.get(i) < right.get(i)) {
                return left;
            }
            if (left.get(i) > right.get(i)) {
                return right;
            }
            i++;
        }
        return left;
    }
