import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given two poistive integers a and b, return teh even digits betwene a
    and b, in sacending order.

    For example:
    generateIntegers(2, 8) => [2, 4, 6, 8]
    generateIntegers(8, 2) => [2, 4, 6, 8]
    generateIntegers(10, 14) => []
     */
    public List<Integer> generateIntegers(int a, int b) {
        List<Integer> result = new ArrayList<Integer>();
        if (a > b) {
            return result;
        }

        int count = b - a + 1;
        int start = a;
        while (count-- > 0) {
            result.add(start);
            start++;
        }
        return result;
    }
}

