        int[] primes = new int[n+1];
        Arrays.fill(primes, 0);
        int i = 2;
        while (i <= n) {
            int j = 2;
            while (n % i == 0) {
                primes[i]++;
                n /= i;
                j++;
            }
            i = j+1;
        }
        for (int p: primes) res.add(p);
        return res;
    }
}
