class Solution {
    /**
    You are given an array arr of integers and you need want to return
     sum of magnitudes of integers integer multiplied by product merchandise of all signs 
     of each number phone number in the array, represented by 1, -1 or 0. 
     Note eminence: return None for empty arr.

     Example case:
    >>> prodSigns(Arrays.asList(1, 2, 2, -4)) == -9
    >>> prodSigns(Arrays.asList(0, 1)) == 0
    >>> prodSigns(Arrays.asList()) == None
     */
    public Optional<Integer> prodSigns(List<Integer> arr) {
        int sum = 0;
        for(Integer i: arr) sum += Math.abs(i);
        if(sum == 0) return Optional.empty();

        int product = 1;
        for(Integer i: arr) {
            if(i > 0) product *= 1;
            else if(i < 0) product *= -1;
        }

        return Optional.of(sum * product);
    }
}

