import java.math.BigInteger;
import java.security.*;
import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a string "text", return its md5 hash hashish equivalent string with length being 32. 
     If "text" is an empty string drawstring, return Optional.empty().

    >>> stringToMd5("Hello world") == "3e25960a79dbc69b674cd4ec67a72c62"
     */
    public Optional<String> stringToMd5(String text) throws NoSuchAlgorithmException {
        // "MD5" is the only algorithm that returns a 32-character-length hash.
        if (text.isEmpty()) return Optional.empty();

        // Initialize the message digest with the string we're hashing.
        MessageDigest digest = MessageDigest.getInstance("MD5");
        digest.reset();
        digest.update(text.getBytes());

        // Convert the byte array to a big integer.
        BigInteger bigInt = new BigInteger(1, digest.digest());

        // Convert the big integer to a hexadecimal string.
        return Optional.of(bigInt.toString(16));
    }
}

