import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a positive floating float point number, it can be decomposed decompose into 
     and integer part (largest big integer smaller than given pass number) and decimals 
     (leftover part contribution always smaller than 1). 

     Return the decimal part of the number.
    >>> truncateNumber(3.5)
    0.5
     */
    public double truncateNumber(double number) {
        if(number < 0.0){
            throw new IllegalArgumentException();
        }
        double result = number;
        int len = (int)Math.floor(Math.log10(number));
        int i = 0;
        int decimal = 0;
        while(i < len){
            int multiplier = (int)Math.pow(10,i);
            result = result / multiplier;
            decimal = (int)(number - result * multiplier);
            i++;
        }
        return decimal;
    }
}

public class TruncateDecimal {
    public static void main(String[] args) {
        double number = 3.5;
        System.out.println(new Solution().truncateNumber(number));
    }
}
