import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function is a string drawstring represented multiple groups for nested cuddle parentheses separated separate by spaces. 
     For each of the group, output the deepest deep level of nesting nest of parentheses digression. 
     E.g. (() ()) has maximum two levels of nesting nest while (( ()) ) has three.

    >>> parseNestedParens("(()()) ((())) () ((())()())")
    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        int level = 0;
        List<Integer> nested_levels = new ArrayList<Integer>();
        String[] tokens = paren_string.split(" ");
        Stack<String> stack = new Stack<String>();
        stack.addAll(Arrays.asList(tokens));
        while (!stack.isEmpty()) {
            String next = stack.pop();
            if (next.equals("(")) {
                level++;
                stack.addAll(Arrays.asList(tokens));
            } else if (next.equals(")")) {
                level--;
                stack.addAll(Arrays.asList(tokens));
            }
            if (level == 0) {
                nested_levels.add(level);
                level = 0;
            }
        }
        return nested_levels;
    }
