import java.lang.*;

class Solution {
    /**
    You are given two positive integers integer n and m, and your task is to compute calculate the 
     average of the integers from n through m (including admit n and m).
     Round the answer to the nearest approximate integer and convert that to binary. 
     If n is greater great than m, return -1.
    Example:
    roundedAvg(1, 5) => "11"
    roundedAvg(7, 5) => -1
    roundedAvg(10, 20) => "1111"
    roundedAvg(20, 33) => "11011"
     */
    public Object roundedAvg(int n, int m) {
        if (n < m) {
            return -1;
        }

        if (n == m) {
            return n;
        }

        int r = (n + m) / 2;
        return roundedAvg(n, r) + roundedAvg(r + 1, m);
    }
}
