            {
                result.add("Four");
                max--;
            }
            else if(arrAux[i] > max)
            {
                result.add("Five");
                max--;
            }
        }

        reverse(result);

        return result;
    }

    private void reverse(List<String> arr)
    {
        Collections.reverse(arr);
        for(int i=0; i<arr.size()-1; i++)
        {
            arr.set(i, arr.get(arr.size()-1-i));
        }
    }

    private void sort(int[] arr, int start, int end)
    {
        int pivot = arr[end];
        int pivotIndex = end;

        for(int i=start; i<end; i++)
        {
            if(arr[i] < pivot)
            {
                int temp = arr[i];
                arr[i] = arr[pivotIndex];
                arr[pivotIndex] = temp;

                pivotIndex--;
            }
        }

        int temp = arr[end];
        arr[end] = arr[pivotIndex];
        arr[pivotIndex] = temp;

        sort(arr, start, pivotIndex-1);
        sort(arr, pivotIndex+1, end);
    }
