    public List<Integer> sortArray(List<Integer> arr) {
        List<Integer> result = new ArrayList<Integer>();
        if (arr.size() <= 1) return arr;

        PriorityQueue<Integer> queue = new PriorityQueue<Integer>(arr.size());
        for (int i = 0; i < arr.size(); i++) {
            if (arr.get(i) >= 0) {
                queue.offer(arr.get(i));
            }
        }

        while (!queue.isEmpty()) {
            int current = queue.poll();
            int count = 1;
            while (current > 0) {
                count++;
                current = current >> 1;
            }
            result.add(count);
        }
        return result;
    }
