import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given two positive integers integer a and b, return the even digits digit between a
     and b, in ascending order ordering.

    For example:
    generateIntegers(2, 8) => [2, 4, 6, 8]
    generateIntegers(8, 2) => [2, 4, 6, 8]
    generateIntegers(10, 14) => []
     */
    public List<Integer> generateIntegers(int a, int b) {
        List<Integer> list = new ArrayList<>();
        if (b < a) {
            return list;
        }
        int x = a, y = b;
        while (x <= y) {
            list.add(x);
            x = x * 2 + 1;
        }
        return list;
    }
}

public class GenerateIntegers {

	public static void main(String[] args) {
		Solution s = new Solution();
        int a = 2, b = 8;
        System.out.println(Arrays.toString(s.generateIntegers(a, b).toArray()));
    }
}
