    public boolean belowZero(List<Integer> operations) {
        int n = operations.size();
        if (n == 0) return false;
        boolean[][] table = new boolean[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i == j) table[i][j] = true;
                else table[i][j] = false;
            }
