class Solution {
    /**
    Input to this function is a string represented multiple groups for nested parenthesis branch by space. 
     For each of the group, output the deep level of nest of parentheses. 
     E.g. (() ()) has maximal two levels of nest while (( ()) ) has three.

    >>> parseNestedParens("(()()) ((())) () ((())()())")
    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        // Check if paren_string is empty.
        if (paren_string.length() == 0) return new ArrayList<Integer>();

        List<Integer> maxLevelList = new ArrayList<Integer>();
        Stack<Integer> maxLevelStack = new Stack<Integer>();
        Stack<Integer> levelStack = new Stack<Integer>();
        levelStack.push(0);

        int i = 0;
        int level = 0;
        while (i < paren_string.length()) {
            if (paren_string.charAt(i) == ')') {
                if (level < levelStack.peek()) {
                    levelStack.push(level);
                    maxLevelStack.push(maxLevelList.size());
                    maxLevelList.add(level);
                }
                level--;
            } else if (paren_string.charAt(i) == '(') {
                level++;
                if (level < levelStack.peek()) {
                    levelStack.push(level);
                    maxLevelStack.push(maxLevelList.size());
                    maxLevelList.add(level);
                }
            }
            i++;
        }

        return maxLevelList;
    }
