    >>> isPrime(6)
    false
    >>> isPrime(101)
    true
    >>> isPrime(11)
    true
    >>> isPrime(13441)
    true
    >>> isPrime(61)
    true
    >>> isPrime(4)
    false
    >>> isPrime(1)
    false
     */
    public boolean isPrime(int n) {
        if (n < 2) return false;
        else if (n < 3) return true;
        else if (n % 2 == 0) return false;
        else if (n == 2) return true;
        else if (n % 3 == 0) return false;
        else {
            int m = (int)Math.sqrt(n);
            for (int i = 5; i <= m; i += 6) {
                if (n % i == 0) return false;
            }
            return true;
        }
    }
}

