    public double findZero(List<Double> xs) {
        return findZero(xs, 0);
    }

    public double findZero(List<Double> xs, int start) {
        int len = xs.size();
        if (start >= len || start % 2!= 0) return Double.NaN;
        double zero = -1, x;
        for (int i = start; i < len; i += 2) {
            x = xs.get(i);
            if (zero < 0 && x!= 0) {
                zero = i;
            } else if (zero >= 0 && x!= 0) {
                return -zero;
            }
        }
        return findZero(xs, start + 1);
    }
}

