            count[c - 'a']++;
        }

        for(int i = 0; i < 26; i++) {
            if(count[i] > 0) {
                res.add(String.valueOf((char) (i + 'a')));
            }
        }

        for(int i = 0; i < txt.length(); i++) {
            if(Character.isWhitespace(txt.charAt(i))) {
