    factorial of this integer.
     */
    public long specialFactorial(int n) {
        if (n < 0) {
            return 0;
        }
        long[] results = new long[n + 1];
        results[0] = 1;
        results[1] = 1;
        for (int i = 2; i <= n; i++) {
            results[i] = results[i - 1] * i;
        }
        return results[n];
    }
