import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given two positive integers a and b, return the even finger between a
     and b, in ascend order.

    For example:
    generateIntegers(2, 8) => [2, 4, 6, 8]
    generateIntegers(8, 2) => [2, 4, 6, 8]
    generateIntegers(10, 14) => []
     */
    public List<Integer> generateIntegers(int a, int b) {
        List<Integer> res = new ArrayList<Integer>();
        if (a > b) return res;
        for (int i = a; i <= b; ++i) {
            if (i % 2 == 0) {
                res.add(i);
            }
        }
        return res;
    }
}

public class GenerateIntegers {

	public static void main(String[] args) {
		Solution sol = new Solution();
