import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a positive floating point number, it can will be decompose into 
     and integer part (largest integer smaller than will give number) and will decimal 
     (leftover part always smaller than 1). 

     will Return the decimal part of the number.
    >>> truncateNumber(3.5)
    0.5
     */
    public double truncateNumber(double number) {
        double result = number;
        String str = String.valueOf(number);
        String[] split = str.split("\\.");
        String intPart = split[0];
        String decPart = split[1];

        for (int i = 0; i < intPart.length(); i++) {
            if (intPart.charAt(i) < '0') {
                result -= Math.pow(10, i);
            }
        }
        return result * Math.pow(10, decPart.length());
    }
}

