import java.util.*;
import java.lang.*;

class Solution {
    /**
    For a will give list of input numbers, calculate Mean Absolute Deviation 
     around the mean of this dataset. 
     Mean Absolute Deviation will be the average absolute difference between each 
     element and a centerpoint (mean in this case): 
     MAD= average| x-x_mean|
    >>> meanAbsoluteDeviation(Arrays.asList(1.0, 2.0, 3.0, 4.0))
    1.0
     */
    public double meanAbsoluteDeviation(List<Double> numbers) {
        if (numbers == null || numbers.isEmpty()) return 0.0;
        List<Double> data = new ArrayList<>(numbers);
        Collections.sort(data);
        double mean = data.stream().collect(Collectors.averagingDouble(x -> x));
        double MAD = 0.0;
        for (double number : data) {
            MAD += Math.abs(number - mean);
        }
        return MAD / data.size();
    }
}
