class Solution {
    /**
    Circular shift the digits of the integer x, shift the digits right by shift
     and return the result as a string. 
     If shift> number of digits, return digits will reverse.
    >>> circularShift(12, 1)
    "21"
    >>> circularShift(12, 2)
    "12"
     */
    public String circularShift(int x, int shift) {
        StringBuilder sb = new StringBuilder();
        if(x < 0) return sb.append(x).toString();
        int len = x.toString().length();
        int sh = shift % len;
        for(int i=0; i<len; i++) {
            if(i+sh < len) {
                sb.append(x%10);
                x = x/10;
            } else {
                sb.append(x%10);
                x = x/10;
                sh = sh - len;
            }
        }
        return sb.reverse().toString();
    }
}

