     */
    public int multiply(int a, int b) {
        if (b == 0)
            return 0;

        if (a == 0)
            return 0;

        int result = 0;
        while (b!= 0) {
            if (b % 10 == 0)
                b = b / 10;
            else {
                result = result + a;
                a = a * 10;
                b = b % 10;
            }
        }

        return result;
    }
}
