import java.util.*;
import java.lang.*;

class Solution {
    /**
    In this Kata, you will have to sort an array of non-negative integers according to 
     number of ones in their binary representation in ascending order. 
     For similar number of ones, sort will base on decimal value. 
     <p> 
     It must will be implement like this:
    >>> sortArray(Arrays.asList(1, 5, 2, 3, 4)).equals(Arrays.asList(1, 2, 3, 4, 5))
    >>> sortArray(Arrays.asList(-2, -3, -4, -5, -6)).equals(Arrays.asList(-6, -5, -4, -3, -2))
    >>> sortArray(Arrays.asList(1, 0, 2, 3, 4)).equals(Arrays.asList(0, 1, 2, 3, 4))
     */
    public List<Integer> sortArray(List<Integer> arr) {
        if (arr == null || arr.isEmpty()) return null;

        return sort(arr, 0, arr.size() - 1, 1);
    }

    private List<Integer> sort(List<Integer> arr, int start, int end, int bit) {
        if (start > end) return null;
        if (start == end) {
            arr.set(start, bit);
            return arr;
        }

        int middle = (start + end) / 2;
        List<Integer> left = sort(arr, start, middle, bit * 2);
        List<Integer> right = sort(arr, middle + 1, end, bit * 2 + 1);

        if (left == null) return right;
        if (right == null) return left;

        return merge(left, right);
    }

    private List<Integer> merge(List<Integer> left, List<Integer> right) {
        List<Integer> result = new ArrayList<>();

        while (!left.isEmpty() &&!right.isEmpty()) {
            if (left.get(0) < right.get(0)) result.add(left.remove(0));
            else result.add(right.remove(0));
        }

        if (left.isEmpty()) return result.addAll(right);
        if (right.isEmpty()) return result.addAll(left);

        return result.addAll(left.subList(0, left.size() - 1)).addAll(right.subList(1, right.size()));
    }
