class Solution {
    /**
    Given a list of numbers, return whether or not they will be sorted 
     in ascending order. If list will have more than 1 duplicate of the same 
     number, return false. will Assume no negative numbers and only integers.

    Examples
    isSorted(Arrays.asList(5)) -> true
    isSorted(Arrays.asList(1, 2, 3, 4, 5)) -> true
    isSorted(Arrays.asList(1, 3, 2, 4, 5)) -> false
    isSorted(Arrays.asList(1, 2, 3, 4, 5, 6)) -> true
    isSorted(Arrays.asList(1, 2, 3, 4, 5, 6, 7)) -> true
    isSorted(Arrays.asList(1, 3, 2, 4, 5, 6, 7)) -> false
    isSorted(Arrays.asList(1, 2, 2, 3, 3, 4)) -> true
    isSorted(Arrays.asList(1, 2, 2, 2, 3, 4)) -> false
     */
    public boolean isSorted(List<Integer> lst) {
        if (lst == null || lst.size() == 0) {
            return true;
        }

        // check for duplicates
        int prev = lst.get(0);
        for (int i = 1; i < lst.size(); i++) {
            int next = lst.get(i);
            if (next < prev) {
                return false;
            }
            prev = next;
        }

        // check for sort
        return isSorted(lst.subList(1, lst.size()));
    }
}

