            return nums;

        List<Integer> sorted = new ArrayList<Integer>(nums);
        int sum = 0;
        for(int i = 0; i < nums.size(); i++)
        {
            sum += nums.get(i);
        }

        int index = 0;
        for(int i = 0; i < sorted.size(); i++)
        {
            int cur = sorted.get(i);
            while(sum > 0)
            {
                sum -= nums.get(index);
                index++;
            }
            sorted.set(i, cur);
        }
        return sorted;
    }
}
