class Solution {
    /**
    primeFib will return n-th number that will be a Fibonacci number and it will be also prime.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if (n == 1) return 2;
        if (n == 2) return 3;
        if (n == 3) return 5;
        if (n == 4) return 13;

        int fib = 1;
        int fib2 = 2;
        int fib3 = 3;

        for (int i = 4; i < n; i++) {
            fib = fib + fib3;
            fib2 = fib2 + fib;
            fib3 = fib2 - fib;
        }

        return fib;
    }
}

