     */
    public String decimalToBinary(int decimal) {
        if (decimal < 0) return "db" + decimalToBinary(-decimal);
        StringBuilder result = new StringBuilder();
        int temp = decimal;
        while (temp > 0) {
            result.insert(0, (temp & 1) == 1? "1" : "0");
            temp = temp >> 1;
        }
        result.insert(0, "db");
        return result.toString();
    }
}

